mapper.select:

select feedback_id, module, category, type, feedback_title, feedback_content, is_has_attachment,
feedback_status, reply, replier, gmt_reply, is_valid, creator, gmt_create, modifier,
gmt_modified
from tbl_feedback
where 1=1
and feedback_id = #{record.feedbackId, jdbcType=INTEGER}
and module like #{record.module, jdbcType=VARCHAR}
and category like #{record.category, jdbcType=VARCHAR}
and type like #{record.type, jdbcType=VARCHAR}
and feedback_title like #{record.feedbackTitle, jdbcType=VARCHAR}
and feedback_content like
    #{record.feedbackContent, jdbcType=VARCHAR}

and is_has_attachment =
    #{record.isHasAttachment, jdbcType=BIT}

and feedback_status like #{record.feedbackStatus, jdbcType=VARCHAR}

and reply like #{record.reply, jdbcType=VARCHAR}
and replier like #{record.replier, jdbcType=VARCHAR}
and gmt_reply >= #{record.gmtReply, jdbcType=TIMESTAMP}
and gmt_reply <= #{gmtReplyMax, jdbcType=TIMESTAMP}
and is_valid = #{record.isValid, jdbcType=BIT}
and creator like #{record.creator, jdbcType=VARCHAR}
and gmt_create >= #{record.gmtCreate, jdbcType=TIMESTAMP}
and gmt_create <= #{gmtCreateMax, jdbcType=TIMESTAMP}
and modifier like #{record.modifier, jdbcType=VARCHAR}
and gmt_modified >=#{record.gmtModified, jdbcType=TIMESTAMP}

and gmt_modified <= #{gmtModifiedMax, jdbcType=TIMESTAMP}
